    pub fn build_lexer<R: Read>() -> Lexer<R> {
        Lexer::new(
            // parameters
            NBR_GROUPS,
            INITIAL_STATE,
            FIRST_END_STATE,
            NBR_STATES,
            // tables
            Box::new(ASCII_TO_GROUP),
            HashMap::<char, GroupId>::from(UTF8_TO_GROUP),
            SegMap::<GroupId>::from_iter(SEG_TO_GROUP),
            Box::new(STATE_TABLE),
            Box::new(TERMINAL_TABLE)
        )
    }

